const INITIAL_STATE = {
   yaziListesi: []
 };

 const Index = (state = INITIAL_STATE, action) => {
   switch (action.type) {
     case 'Home':
       return { ...state, yaziListesi: action.payload };
     default:
       return state;
   }
 };

 export default Index;
