import React, { useState } from "react";
import "./login.css";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { FetchLogin } from "../actions/auth";
const Login = () => {
  const dispatch = useDispatch();


  const [data, setData] = useState({
      email: "",
      password: ""
  });

  const handleChange = (e) => {
      setData({ ...data, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
      e.preventDefault();

      try {
          // API çağrısını dispatch ediyoruz ve yanıtı alıyoruz
          const response = await dispatch(FetchLogin(data));

          if (response.payload?.email) {
              // Hata mesajını gösteriyoruz
              alert(response.payload.email);
          } else {
            alert("böyle bir email kayıtlı değil");
          }
      } catch (error) {
          console.error('Error during login:', error);
          alert('Bir hata oluştu');
      }
  };
  return (
    <>
    <div className="login-form"   style={{backgroundColor:"red"}}>
            <h2>Giriş Yap</h2>
            <form onSubmit={handleSubmit}>
                <div    >
                    <label>Email:</label>
                    <input
                        type="email"
                        name="email"
                        value={data.email}
                        onChange={handleChange}

                    />
                </div>
                <div>
                    <label>Parola:</label>
                    <input
                        type="password"
                        name="password"
                        value={data.password}
                        onChange={handleChange}
                    />
                </div>
                <button type="submit">Giriş Yap</button>
            </form>
        </div>
      <div className="container-fluid p-0 m-0">
        <div className="row">
