const INITIAL_STATE={
    auth:{}
}

const auth = (state=INITIAL_STATE,action) => {
 switch (action.type) {
    case "auth":
        return {...state,yaziListesi:action.payload}
        break;

    default:
        break;
 }
}

export default auth
