import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {fetchHomeData} from "../actions/home";
const Section = () => {
  const dispatch = useDispatch();
  const yaziListesi = useSelector((state) => state.yaziListesi);

  useEffect(() => {
    dispatch(fetchHomeData());
  }, [dispatch]);
  return (
    <div class="middle-section">
      <div class="top-nav">
        <div class="search-flex">
          <div class="search-bar">
            <img src="./assets/search.svg" class="search-icon" alt="" />
            <input type="text" placeholder="Search Twitter" />
          </div>
          <img src="./assets/setting.svg" class="setting-icon" alt="" />
        </div>

        <div class="tab">
          <button class="tablinks" onclick="switchTabs(event, 'Trending')">
            For You
          </button>
          <button class="tablinks" onclick="switchTabs(event, 'Trending')">
            Trending
          </button>
        </div>
      </div>

      <div id="ForYou" class="tabcontent">
        {yaziListesi.map((item,index)=>{
          return(
            <>

          <div class="trending-box">
            <p class="trending-heading">{item.baslik}</p>
            <p class="trending-text">{item.acikla}</p>
            <p class="trending-heading">
              Trending with{" "}
              {item.tags.map((data)=>{
                return(
                  <a href="#" class="twitter-links">
                   {data.tags}
                  </a>
                )
              })}

            </p>
          </div>

          <div
            class="tabs-main-img"
            style={{
              backgroundImage: `linear-gradient(
                  to bottom,
                  rgba(0, 0, 0, 0),
                  rgba(0, 0, 0, 0.7)
                ),
                url(${item.selectedFile})`,
            }}
          >
                 <h3>{item.baslik}</h3>
                 <p>{item.acikla}</p>
          </div>
          </>
          )
        })}


      </div>


    </div>
  );
};

export default Section;
