// src/reducer/RootReducer.js
import { combineReducers } from "redux";
import authReducer from "./auth"; // Adjust the import path as necessary
import indexReducer from "./index"; // Adjust the import path as necessary

const rootReducer = combineReducers({
  Auth: authReducer,
  Index: indexReducer,
});

export default rootReducer;
