import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Link } from "react-router-dom";
import { FetchLogin } from "../actions/auth";
import "./login.css";

const Login = () => {
  const dispatch = useDispatch();
  const [data, setData] = useState({ email: "", password: "" });

  const handleChange = (e) => {
    setData({ ...data, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await dispatch(FetchLogin(data));
      console.log('Response in handleSubmit:', response); // Yanıtı kontrol etmek için ekleyin
      if (response?.token) {
        alert(`Hoş geldin ${response.email}`);
      } else {
        alert("Böyle bir email kayıtlı değil");
      }
    } catch (error) {
      console.error('Error during login:', error);
      alert('Bir hata oluştu');
    }
  };

  return (
    <div className="container-fluid p-0 m-0">
      <div className="row">
        <div className="col-md-7" style={{ width: "50%", height: "100vh" }}>
          <img
            src="./assets/cover-img.png"
            alt=""
            style={{ height: "100%" }}
          />
        </div>
        <div
          className="col-md-5"
          style={{
            justifyContent: "center",
            alignItems: "center",
            display: "flex",
          }}
        >
          <div className="logo">
            <img src="./assets/logo.png" alt="Logo" />
            <main className="" style={{ width: "600px" }}>
              <h1>Twitter Giriş Yap</h1>
              <div className="login-form" style={{ backgroundColor: "red" }}>
                <h2>Giriş Yap</h2>
                <form onSubmit={handleSubmit}>
                  <div>
                    <label>Email:</label>
                    <input
                      type="email"
                      name="email"
                      value={data.email}
                      onChange={handleChange}
                    />
                  </div>
                  <div>
                    <label>Parola:</label>
                    <input
                      type="password"
                      name="password"
                      value={data.password}
                      onChange={handleChange}
                    />
                  </div>
                  <button type="submit">Giriş Yap</button>
                </form>
                <div className="links container">
                  <a target="_blank" href="https://twitter.com/login?lang=pt">
                    üye değilsen,
                  </a>
                  <span className="ponto"></span>
                  <Link to="/üye-ol">Üye Ol</Link>
                </div>
              </div>
            </main>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
