const INITIAL_STATE = {
   yaziListesi: []
 };

 const Home = (state = INITIAL_STATE, action) => {
   switch (action.type) {
     case 'Home':
       return { ...state, yaziListesi: action.payload };
     default:
       return state;
   }
 };

 export default Home;
