import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {fetchHomeData} from "../actions/home";
const Section = () => {
  const dispatch = useDispatch();
  const yaziListesi = useSelector((state) => state.yaziListesi);

  useEffect(() => {
    dispatch(fetchHomeData());
  }, [dispatch]);
  return (
    <div class="middle-section">
      <div class="top-nav">
        <div class="search-flex">
          <div class="search-bar">
            <img src="./assets/search.svg" class="search-icon" alt="" />
            <input type="text" placeholder="Search Twitter" />
          </div>
          {yaziListesi.map((item, index) => {
          return (
            <>
            <div class="trending-box"  key={item.id}>
              <p class="trending-heading">{item.baslik}</p>
              <p class="trending-text">#earthquake</p>
              <p class="trending-heading">{item.acikla}</p>
            </div>

          <img src={item.selectedFile} class="setting-icon" alt="" />
          </>
        );
      })}
        </div>

        <div class="tab">
          <button class="tablinks" onclick="switchTabs(event, 'Trending')">
            For You
          </button>
          <button class="tablinks" onclick="switchTabs(event, 'Trending')">
            Trending
          </button>
        </div>
      </div>

      <div id="ForYou" class="tabcontent">
        <div class="tabs-main-img">
          <h3>Trending For You</h3>
          <p>Building your tech career @ AccioJob</p>
        </div>
        <div class="trending-box">
          <p class="trending-heading">Trending In India</p>
          <p class="trending-text">#earthquake</p>
          <p class="trending-heading">
            Trending with{" "}
            <a href="#" class="twitter-links">
              #BREAKING_NEWS
            </a>
          </p>
        </div>

        <div
          class="tabs-main-img"
          style={{
            backgroundImage: `linear-gradient(
                to bottom,
                rgba(0, 0, 0, 0),
                rgba(0, 0, 0, 0.7)
              ),
              url('assets/pollution.jpeg')`,
          }}
        >
          <h3>Pollution</h3>
          <p>Pollution in Delhi is rising.</p>
        </div>
        <div class="trending-box">
          <p class="trending-heading">Pollution Trends</p>
          <p class="trending-text">#pollution</p>
          <p class="trending-heading">1.418k Tweets</p>
        </div>
      </div>


    </div>
  );
};

export default Section;
