const INITIAL_STATE={
    auth:null,
    error: null,
}

const Login = (state=INITIAL_STATE,action) => {
 switch (action.type) {
    case "auth":
        return {...state,auth:action.payload}

 case "authLogin":
    return {...state,auth:action.payload, error: null}
    case "AUTH_LOGIN_FAILURE":
        return {
            ...state,
            auth: null,
            error: action.payload,
        };
        default:
            return state;
 }
}

export default Login
