import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { Detay } from "../actions/home";

const Detail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const item = useSelector((state) => state.yaziDetay);

  useEffect(() => {
    dispatch(Detay(id));
  }, [dispatch]);
  return (
    <div className="middle-section">
      <div className="top-nav">
        <div className="search-flex">
          <div className="search-bar">
            <img src="./assets/search.svg" className="search-icon" alt="" />
            <input type="text" placeholder="Search Twitter" />
          </div>
          <img src="./assets/setting.svg" className="setting-icon" alt="" />
        </div>
      </div>

      <div id="ForYou" className="tabcontent">
        <div className="trending-box" style={{ cursor: "pointer" }}>
          <p className="trending-heading">{item.baslik}</p>
          <p className="trending-text">{item.acikla}</p>
          <p className="trending-heading"></p>
        </div>

        <div
          className="tabs-main-img"
          style={{
            backgroundImage: `linear-gradient(
                to bottom,
                rgba(0, 0, 0, 0),
                rgba(0, 0, 0, 0.7)
              ),
              url(${item.selectedFile})`,
          }}
        >
          <h3>{item.baslik}</h3>
          <p>{item.acikla}</p>
        </div>
      </div>
      <div
        className="input-container"
        style={{


          alignItems: "center",

          width: "100%",
        }}
      >
        <input
          type="text"
          className="input-field"
          placeholder="Type something..."
          style={{
            flex: 1 /* Take remaining space */,

            padding: "8px",

            fontSize: "16px",

            border: "1px solid #ccc",

            borderRadius: "4px",

            marginRight: "5px",
          }}
        />
        <button
          className="action-button"
          style={{
            padding: " 8px 12px",

            fontSize: "16px",

            backgroundColor: " #007bff" /* Adjust button color */,

            color: "white",
            border: "none",
            borderRadius: "4px",

            cursor: "pointer",
          }}
        >
          GÃ¶nder
        </button>
        <ul className="result-list">
          <li>Result 1</li>
          <li>Result 2</li>
          <li>Result 3</li>
        </ul>
      </div>
    </div>
  );
};

export default Detail;
